import './App.css';
import React, { useEffect, useState } from 'react'
import axios from 'axios';
import oneStudent from './components/Student';

const StudentApp = () => {
  const [student, setStudent] = useState([]);

  //calling api
  useEffect(() => {
      axios.get('https://api.hatchways.io/assessment/students')
          .then(res => setStudent(res.data.students));
  }, [])

  const average = (arr) => {
    let sum = 0;
    for (let i = 0; i < arr.length; i++) {
      sum += parseInt(arr[i]);
    }
    let total = sum / arr.length;

    return total;
  }
  return (
    <div >
      {student.map((student, idx) => {return(<oneStudent></oneStudent>) })}
    </div>
  );
}

export default StudentApp;
