import './App.css';

import React, { useState, useEffect } from 'react';
let scroll = 0;
let wheel = 0;
const Portfolio = () => {
  const [firstBox, setFirstBox] = useState(0)
  const [contVis, setContVis] = useState('')
  const [boxVis1, setBoxVis1] = useState('')
  const [boxVis2, setBoxVis2] = useState('')
  const [boxVis3, setBoxVis3] = useState('')
  const [waveani, setWaveAni] = useState('')

const handleScroll = (e) =>{
  let y = e.deltaY;
  console.log(y)
  console.log(scroll)
  console.log(wheel)
  console.log(firstBox)
  if(y > 0){
    scroll++;
    if(wheel == 0){
      if(scroll > wheel){
        wheel=1;
        scroll=1;
        setFirstBox(wheel)

      }
    }else if(wheel == 1){
      if(scroll>wheel){
        wheel=2;
        scroll = 2;
        setFirstBox(wheel)
      }
    }else if(wheel == 2){
      if(scroll>wheel){
        wheel=3;
        scroll = 3;
        setFirstBox(wheel)

      }else if(scroll>3){
        scroll = 3;
      }
    }
  }else if(y < 0){
    scroll--;
    if(wheel == 1){
      if(scroll < wheel){
        wheel=0;
        scroll=0;
        setFirstBox(wheel)

      }
    }else if(wheel == 2){
      if(scroll<wheel){
        wheel=1;
        scroll = 1;
        setFirstBox(wheel)

      }
    }else if(wheel == 3){
      if(scroll<wheel){
        wheel=2;
        scroll = 2;
        setFirstBox(wheel)

      }
    }
  }
  if (firstBox == 1){
    setContVis('content')
    setBoxVis1('box1')
    setBoxVis2('box2')
    setBoxVis3('box3')
    setWaveAni('waves1')
  }else if(firstBox == 2){
    setContVis('content2')
    setBoxVis1('box4')
    setBoxVis2('box5')
    setBoxVis3('box6')
    setWaveAni('waves2')
  }else if(firstBox == 3){
    setContVis('content3')
    setBoxVis1('box7')
    setBoxVis2('box8')
    setBoxVis3('box9')
    setWaveAni('waves3')
  }
}
  return (
    <div className = 'Night' onWheel={handleScroll}>
      <div className = 'Title'>
        <h1>Sam Losh</h1>
        <h4>Web designer</h4>
      </div>
      <div className = 'sky'>
        <div className = 'cloud1'></div>
        <div className = 'cloud2'></div>
        <div className = 'cloud3'></div>
        <div className = 'cloud4'></div>
        <div className = 'cloud5'></div>
        <div className = 'cloud6'></div>
        <div className = 'cloud7'></div>
        <div className = 'cloud8'></div>
        <div className = 'moon'></div>
        <div className = 'spot1'></div>
        <div className = 'spot2'></div>
        <div className = 'spot3'></div>
      </div>
      <div className = 'land1'>
        <div className = 'mountain1'></div>
        <div className = 'shade1'></div>
        <div className = 'mountain2'></div>
        <div className = 'shade2'></div>
        <div className = 'mountain3'></div>
        <div className = 'mountain4'></div>
      </div>
      <div className = 'land2'>
        <div className = 'mountain5'></div>
        <div className = 'mountain6'></div>
        <div className = 'mountain7'></div></div>

        <div className={contVis} >
          <div className={boxVis1}></div>
          <div className={boxVis2}></div>
          <div className={boxVis3}></div>
        </div>

        <div className={contVis} >
          <div className={boxVis1}></div>
          <div className={boxVis2}></div>
          <div className={boxVis3}></div>
        </div>

        <div className={contVis} >
          <div className={boxVis1}></div>
          <div className={boxVis2}></div>
          <div className={boxVis3}></div>
        </div>

        <div className = 'water' >
          <div className={waveani}></div>
          <div className={waveani}></div>
          <div className={waveani}></div>
          <div className={waveani}></div>
          <div className={waveani}></div>
        </div>
    </div>
  );
}

export default Portfolio;
