import React, { useEffect, useState } from 'react'

const oneStudent = () =>{

    const [testScores, setTestScores] = useState(false);

    return(<div className='wrapper' key={idx}>
    <img src={student.pic} className='pfp'></img>
    
    <div className='infoWrapper'>
      <h1>{student.firstName} {student.lastName}</h1>

      <div className="info"> Email: {student.email} </div>
      <div className="info"> Company: {student.company} </div>
      <div className="info"> Skill: {student.skill} </div>
      <div className="info"> Average: {average(student.grades)}%</div>

      <div>
        {testScores &&
          student.grades.map((grades, idx) => {
            return (
              <div className= 'testscores' key={grades + " " + idx}>
                <div>
                  {" "}
                  Test {idx + 1} : {grades}%{" "}
                </div>
              </div>
            );
          })}
      </div>
    </div>

    <button
      className="button"
      type='button'
      onClick={() => {
        setTestScores(!testScores);
      }}>
      {testScores ? "-" : "+"}
    </button>
  </div>)
}

export default oneStudent;
