import * as React from "react";
import { useState } from 'react';
import  Link from "react-router-dom";

let scroll = 0;
let wheel = 0;
let scrollingDirection = 0; //idle
const Portfolio = () => {
  const [contVis, setContVis] = useState('')
  const [boxVis1, setBoxVis1] = useState('')
  const [boxVis2, setBoxVis2] = useState('')
  const [boxVis3, setBoxVis3] = useState('')
  const [waveani, setWaveAni] = useState('')

  const change=()=>{setTimeout(() => {
    scrollingDirection = 0;
    console.log('BLLOOOPP')
  }, 2500);
}

const handleScroll = (e) =>{
  let y = e.deltaY;
  if(y > 1 && scrollingDirection != 1 ){
    scroll++;
    if(wheel == 0){
      if(scroll > wheel){
        scrollingDirection = 1;
        wheel=1;
        scroll=1;
        setContVis('content');
        setBoxVis1('box1');
        setBoxVis2('box2');
        setBoxVis3('box3');
        setWaveAni('waves1');
        change()
      }
    }else if(wheel == 1){
      if(scroll>wheel){
        scrollingDirection = 1;
        wheel=2;
        scroll = 2;
        setContVis('content2')
        setBoxVis1('box4')
        setBoxVis2('box5')
        setBoxVis3('box6')
        setWaveAni('waves2')
        change()
      }
    }else if(wheel == 2 || wheel == 3){
      if(scroll>wheel){
        wheel=3;
        scroll = 3;
    setContVis('content3')
    setBoxVis1('box7')
    setBoxVis2('box8')
    setBoxVis3('box9')
    setWaveAni('waves3')
    change()
}}}else if(y < -1 && scrollingDirection != 1){
    if(wheel == 1){
      scroll--;
      if(scroll < wheel){
        wheel=1;
        scroll=1;
        
      }
    }else if(wheel == 2){
      scroll--;
      if(scroll<wheel){
        scrollingDirection = 1;
        wheel=1;
        scroll=1;
        setContVis('content');
        setBoxVis1('box1');
        setBoxVis2('box2');
        setBoxVis3('box3');
        setWaveAni('waves1');
        change()
      }
    }else if(wheel == 3){
      scroll--;
      if(scroll<wheel){
        scrollingDirection = 1;
        wheel=2;
        scroll = 2;
        setContVis('content2')
        setBoxVis1('box4')
        setBoxVis2('box5')
        setBoxVis3('box6')
        setWaveAni('waves2')
        change()
        
      }
    }
  }
}

  return (
    <div className = 'Night' onWheel={handleScroll}>
      <div className = 'Title'>
        <h1>Sam Losh</h1>
        <h4>Web designer</h4>
      </div>
      <div className = 'sky'>
        <div className = 'cloud1'></div>
        <div className = 'cloud2'></div>
        <div className = 'cloud3'></div>
        <div className = 'cloud4'></div>
        <div className = 'cloud5'></div>
        <div className = 'cloud6'></div>
        <div className = 'cloud7'></div>
        <div className = 'cloud8'></div>
        <div className = 'moon'></div>
        <div className = 'spot1'></div>
        <div className = 'spot2'></div>
        <div className = 'spot3'></div>
      </div>
      <div className = 'land1'>
        <div className = 'mountain1'></div>
        <div className = 'shade1'></div>
        <div className = 'mountain2'></div>
        <div className = 'shade2'></div>
        <div className = 'mountain3'></div>
        <div className = 'mountain4'></div>
      </div>
      <div className = 'land2'>
        <div className = 'mountain5'></div>
        <div className = 'mountain6'></div>
        <div className = 'mountain7'></div></div>

        <div className={contVis}  >
          <div className={boxVis1}></div>
          <div className={boxVis2}></div>
          <div className={boxVis3}></div>
        </div>
        <div className='navbar'>
          <button className='navbutt'></button>
          <div className='navbox'>
              <Link to='/About'>About</Link>
              <Link to='/Sample'>Sample</Link>
              <Link to='/Contact'>Contact</Link>
          </div>
        </div>
        <div className = 'water' >
          <div className={waveani}></div>
          <div className={waveani}></div>
          <div className={waveani}></div>
          <div className={waveani}></div>
          <div className={waveani}></div>
        </div>
    </div>
  );
}

export default Portfolio;
