import './App.css';
import React, { useEffect, useState } from 'react'
import axios from 'axios';

const Students = () => {
  const [student, setStudent] = useState([]);
  const [testScores, setTestScores] = useState(false);

  //calling api
  useEffect(() => {
      axios.get('https://api.hatchways.io/assessment/students')
          .then(res => setStudent(res.data.students));
  }, [])

  const average = (arr) => {
    let sum = 0;
    for (let i = 0; i < arr.length; i++) {
      sum += parseInt(arr[i]);
    }
    let total = sum / arr.length;

    return total;
  }
  return (
    <div>
      {student.map((student, idx) => {return <div key={idx}>
        <img src={student.pic} className='pfp'></img>
        <button
          className="button"
          onClick={() => {
            setTestScores(!testScores);
          }}
        >
          {testScores ? "-" : "+"}
        </button>
        <h1>{student.firstName} {student.lastName}</h1>

        <div className="info"> Email: {student.email} </div>
        <div className="info"> Company: {student.company} </div>
        <div className="info"> Skill: {student.skill} </div>
        <div className="info"> Average: {average(student.grades)}%</div>

        <div>
          {testScores &&
            student.grades.map((grades, idx) => {
              return (
                <div className= 'testscores' key={grades + " " + idx}>
                  <div>
                    {" "}
                    Test {idx + 1} : {grades}%{" "}
                  </div>
                </div>
              );
            })}
        </div>
      </div>})}
    </div>
  );
}

export default Students;
