import React, { useState } from 'react'

const OneStudent = ({id,pic,firstName,lastName,email,company,skill,grades,student}) =>{

    const [testScores, setTestScores] = useState(false);

    const average = (arr) => {
        let sum = 0;
        for (let i = 0; i < arr.length; i++) {
            sum += parseInt(arr[i]);
        }
        let total = sum / arr.length;
        
        return total;
    }

    return(<div className='wrapper' key={idx}>
    <img src={pic} className='pfp'></img>
    
    <div className='infoWrapper'>
    <h1>{firstName} {lastName}</h1>

    <div className="info"> Email: {email} </div>
    <div className="info"> Company: {company} </div>
    <div className="info"> Skill: {skill} </div>
    <div className="info"> Average: {average(grades)}%</div>

    <div>
        {testScores && grades.map((grades, idx) => {
            return (
                <div className= 'testscores' key={grades + " " + idx}>
                    <div>
                        {" "}
                        Test {idx + 1} : {grades}%{" "}
                    </div>
                </div>
            );})}
        </div>
    </div>

    <button className="button" type='button' onClick={() => {setTestScores(!testScores);}}>
            {testScores ? "-" : "+"}
    </button>
  </div>)
}

export default OneStudent;
